package day9;

public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=2000;
		double d=a; //implicit casting
		System.out.println("implicit casting : "+d);
		
		
		double d1=100.89;
		int b =(int)d1;
		System.out.println(b);

	}

}
